ROW (5, "Egg Bomb Upgrade", 'Ranged', 'Move1', 12, 9, "Also slows enemies by 40% for 2s after they're thrown.", 'Special', 0.41, 21, 460),
ROW (5, "Helping Hand", 'Buff', 'Move1', 6, 9, "Blissey greatly improves movement speed and auto attack speed for self and allies by 35% for 3s. During this duration, all of Blissey's auto attacks are boosted. After this bonus expires, Blissey loses any boosted attack that was granted.", NULL, NULL, NULL, NULL),
ROW (5, "Helping Hand Upgrade", 'Buff', 'Move1', 12, 9, "Also increases auto attack damage for all those affected by 15% for 3s.", NULL, NULL, NULL, NULL),
ROW (5, "Heal Pulse", 'Recovery', 'Move2', 1, 10, "Blissey continually restores HP as well as the HP of a designated ally for 2s, healing every 0.5s.", NULL, NULL, NULL, NULL),
ROW (5, "Soft-boiled", 'Recovery', 'Move2', 4, 12, "Blissey throws an egg to an ally, healing Blissey and the designated ally. Additionally healing every 1s for 2s afterwards. Additional Soft Boiled uses before these 2s will overwrite the previous egg's additional healing over time. Holds 2 uses in reserve and cannot be used more than once every 2s.

